import { useState, useEffect } from 'react';

function App() {
  const [formData, setFormData] = useState({ name: '', file: null });
  const [records, setRecords] = useState([]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();
    data.append('name', formData.name);
    data.append('file', formData.file);
    await fetch('http://localhost:5555/sample_ppt', { method: 'POST', body: data });
    fetchRecords();
  };

  const fetchRecords = async () => {
    const res = await fetch('http://localhost:5555/sample_ppt/records');
    setRecords(await res.json());
  };

  useEffect(() => fetchRecords(), []);

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input placeholder="Name" required onChange={(e) => setFormData({ ...formData, name: e.target.value })} />
        <input type="file" required onChange={(e) => setFormData({ ...formData, file: e.target.files[0] })} />
        <button type="submit">Submit</button>
      </form>
      <table>{records.map(({ id, name, file_path }) => (
        <tr key={id}><td>{name}</td>
        <td><img src={`http://localhost:5555/uploads/${file_path}`} alt={name} style={{ maxWidth: '100px' }} /></td></tr>
      ))}</table>
    </div>
  );
}

export default App;
