import React, { useState } from 'react';
import fs from 'fs';
import path from 'path';

function App() {
  const [file, setFile] = useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (file) {
      const filePath = path.join(__dirname, 'uploads', file.name);
      fs.copyFile(file.path, filePath, (err) => {
        if (err) throw err;
        alert('File uploaded successfully');
      });
    }
  };

  return (
    <div className="flex justify-center items-center h-screen bg-gray-100">
      <form onSubmit={handleSubmit} className="bg-white p-6 rounded shadow-md">
        <input type="file" onChange={handleFileChange} className="mb-4 border border-gray-300 p-2 rounded" />
        <button type="submit" className="bg-blue-500 text-white p-2 rounded hover:bg-blue-600">Upload</button>
      </form>
    </div>
  );
}

export default App;
