import {useState ,createContext} from 'react';

export const Change_Page = createContext();
const Page = ({ children }) => {
    const [displayPage,setDisplayPage] = useState('Register');
    return (
        <Change_Page.Provider value={{displayPage,setDisplayPage}}>
            {children}
        </Change_Page.Provider>
    );
}

export default Page;
