import React, { useState } from 'react';

function Country({ Pages }) {
    const [countryCode, setCountryCode] = useState('');
    const [countryName, setCountryName] = useState('');
    const [errors, setErrors] = useState({});

    const handleInputChange = (e) => {
        const { id, value } = e.target;
        if (id === 'countryCode') setCountryCode(value);
        else if (id === 'countryName') setCountryName(value);
        
        // Reset error for the input being changed
        setErrors((prevErrors) => ({ ...prevErrors, [id]: '' }));
    };

    const validateForm = () => {
        const formErrors = {};
        
        // Validation for Country Code
        if (!countryCode) formErrors.countryCode = "Country Code is required.";
        else if (countryCode.length !== 2) formErrors.countryCode = "Country Code should be exactly 2 characters.";

        // Validation for Country Name
        if (!countryName) formErrors.countryName = "Country Name is required.";
        else if (countryName.length < 3) formErrors.countryName = "Country Name should be at least 3 characters.";

        setErrors(formErrors);
        return Object.keys(formErrors).length === 0;
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (validateForm()) {
            alert("Country added/updated successfully!");
            // Clear the form
            setCountryCode('');
            setCountryName('');
        }
    };

    return (
        <div className="bg-gray-100 flex items-center justify-center h-screen">
            <div className="bg-white p-6 rounded shadow-md w-96">
                <button onClick={() => { Pages('Register') }} type="button" className="rounded border-gray-300 bg-gray-500 text-white p-2 mb-4">
                    Back
                </button>
                <h2 className="text-2xl font-bold mb-4">Country Management</h2>
                <form id="countryForm" onSubmit={handleSubmit}>
                    <div className="mb-4">
                        <label htmlFor="countryCode" className="block text-sm font-medium text-gray-700">Country Code (Editable)</label>
                        <input 
                            type="text" 
                            id="countryCode" 
                            value={countryCode} 
                            onChange={handleInputChange} 
                            className="mt-1 block w-full p-2 border border-gray-300 rounded" 
                             
                        />
                        {errors.countryCode && <p className="text-red-500 text-sm">{errors.countryCode}</p>}
                    </div>
                    <div className="mb-4">
                        <label htmlFor="countryName" className="block text-sm font-medium text-gray-700">Country Name</label>
                        <input 
                            type="text" 
                            id="countryName" 
                            value={countryName} 
                            onChange={handleInputChange} 
                            className="mt-1 block w-full p-2 border border-gray-300 rounded" 
                             
                        />
                        {errors.countryName && <p className="text-red-500 text-sm">{errors.countryName}</p>}
                    </div>
                    <button type="submit" className="bg-blue-500 text-white font-bold py-2 px-4 rounded hover:bg-blue-600">
                        Add/Update Country
                    </button>
                </form>

                <h2 className="text-xl font-bold mt-6 mb-4">Countries List</h2>
                <table className="min-w-full bg-white border border-gray-300">
                    <thead>
                        <tr>
                            <th className="py-2 border-b text-left">Default ID</th>
                            <th className="py-2 border-b text-left">Country Code</th>
                            <th className="py-2 border-b text-left">Country Name</th>
                            <th className="py-2 border-b text-left">Actions</th>
                        </tr>
                    </thead>
                    <tbody id="countryTableBody"></tbody>
                </table>
            </div>
        </div>
    );
}

export default Country;
