import {useState ,createContext} from 'react';

export const Change_Page = createContext();
const Page = ({ children }) => {
    const [Show,setshow] = useState('Register');
    return (
        <Change_Page.Provider value={{show,setshow}}>
            {children}
        </Change_Page.Provider>
    );
}

export default Page;
