import React, { useState } from "react";
import { useNavigate } from 'react-router-dom';
import { dbx } from '../App';  // Import dbx from App.js


function Country() {
  const navigate = useNavigate();
  const [formdata, setformdata] = useState({ countryCode: '', countryName: '' });
  const [errors, setErrors] = useState({});
  const [displaydata,setdisplaydata]=useState([]);


  const fun = (e) => {
    const { id, value } = e.target;
    setformdata({ ...formdata, [id]: value });
    setErrors((prevErrors) => ({ ...prevErrors, [id]: "" })); // Reset the error for the specific input
  };

  const validateForm = () => {
    const formErrors = {};
    if (!formdata.countryCode) {
      formErrors.countryCode = "Country Code is required.";
    } else if (formdata.countryCode.length !== 2) {
      formErrors.countryCode = "Country Code should be exactly 2 characters.";
    }

    if (!formdata.countryName) {
      formErrors.countryName = "Country Name is required.";
    } else if (formdata.countryName.length < 3) {
      formErrors.countryName = "Country Name should be at least 3 characters.";
    }

    setErrors(formErrors);
    return Object.keys(formErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      alert("Country added/updated successfully!");
      console.log(formdata);
      setformdata({ countryCode: '', countryName: '' });
      if(dbx) {const countryadd= dbx.transaction("country","readwrite").objectStore("country").add(formdata);
      countryadd.onsuccess = (e) => { console.log("Country added/updated successfully!");
        // navigate('/country'); // Redirect to country page after successful submission
      };} else {console.log("dbx error(not inited)");}
       // Clear the form state after submission
    }
  };

  function fetch (){
    if(dbx) {const countryget= dbx.transaction("country","readonly").objectStore("country").getAll();
      countryget.onsuccess = (e) => {
        setdisplaydata(e.target.result);
        console.log("Data fetched successfully!");
      };
      countryget.onerror = (e) => {
        console.error("Error fetching data:", e.target.error);
      };
    } else {console.log("dbx error(not inited)");}

  }

  const handleDelete = (id) => { 
    if (!id){console.log("Delete ID: " + id);}
    if (dbx) {const what_del=dbx.transaction("country","readwrite").objectStore("country").delete(id);
    what_del.onsuccess = (e) => {fetch(e.target.result);};
    what_del.onerror = (e) => {console.error("Error deleting data:", e.target.error);};}}

  return (
    <div className="bg-gray-100 flex items-center justify-center h-screen">
      <div className="bg-white p-6 rounded shadow-md w-96">
        <button
          onClick={() => navigate('/')}
          type="button"
          className="rounded border-gray-300 bg-gray-500 text-white p-2 mb-4"
        >
          Back
        </button>
        <h2 className="text-2xl font-bold mb-4">Country Select</h2>
        <form id="countryForm" onSubmit={handleSubmit}>
          <div className="mb-4">
            <label htmlFor="countryCode" className="block text-sm font-medium text-gray-700">
              Country Code
            </label>
            <input
              type="text"
              id="countryCode"
              value={formdata.countryCode}
              onChange={fun}
              className="mt-1 block w-full p-2 border border-gray-300 rounded"
            />
            {errors.countryCode && (
              <p className="text-red-500 text-sm">{errors.countryCode}</p>
            )}
          </div>
          <div className="mb-4">
            <label htmlFor="countryName" className="block text-sm font-medium text-gray-700">
              Country Name
            </label>
            <input
              type="text"
              id="countryName"
              value={formdata.countryName}
              onChange={fun}
              className="mt-1 block w-full p-2 border border-gray-300 rounded"
            />
            {errors.countryName && (
              <p className="text-red-500 text-sm">{errors.countryName}</p>
            )}
          </div>
          <button
            type="submit"
            className="bg-blue-500 text-white font-bold py-2 px-4 rounded hover:bg-blue-600"
          >
            Add/Update Country
          </button>

        </form>

        <h2 className="text-xl font-bold mt-6 mb-4">Countries List</h2>
        <button onClick={fetch} className="bg-blue-500 text-white font-bold py-2 px-4 rounded hover:bg-blue-600">Load Countries</button>
        <table className="min-w-full bg-white border border-gray-300">
          <thead>
            <tr>
              <th className="py-2 px-2 border-b text-left">Default ID</th>
              <th className="py-2 px-2 border-b text-left">Country Code</th>
              <th className="py-2 px-2 border-b text-left">Country Name</th>
              <th className="py-2 px-2 border-b text-left">Edit</th>
              <th className="py-2 px-2 border-b text-left">Delete</th>
            </tr>
          </thead>
          <tbody id="countryTableBody"></tbody>

          {displaydata.map((value,ind)=>(<tr key={ind}>
            <td>{value.id}</td>

            <td>{value.countryCode}</td>
            <td>{value.countryName}</td>
            <td  ><button onclick={handleDelete(value.id)}>Edit</button></td>
            <td ><button>Delete</button></td>

            </tr>
          )

          )}

        </table>
      </div>
    </div>
  );
}

export default Country;
