// store.js
import { createStore } from 'redux';

// Initial state
const shelf = { car_count: 0 };

// Reducer function to handle actions
function car_func(state = shelf, action) {
    switch (action.type) {
        case 'ADD':
            return { car_count: state.car_count + 1 };
        default:
            return state;
    }
}

// Create the store with the reducer
const store = createStore(car_func);

export default store;
