import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { addTask, editTask, deleteTask, loadTasks } from "../redux/stateSlice";

function State() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({ country: "", stateName: "" });
  const [errors, setErrors] = useState({});
  const [editId, setEditId] = useState(null);
  const dispatch = useDispatch();
  const tasks = useSelector((state) => state.state.tasks); // Get states list from Redux

  const handleInputChange = (e) => {
    const { id, value } = e.target;
    setFormData({ ...formData, [id]: value });
    setErrors((prevErrors) => ({ ...prevErrors, [id]: "" })); // Clear error for the input
  };

  const validateForm = () => {
    const formErrors = {};
    if (!formData.country) {
      formErrors.country = "Please select a country.";
    }
    if (!formData.stateName) {
      formErrors.stateName = "State name is required.";
    } else if (formData.stateName.length < 3) {
      formErrors.stateName = "State name should be at least 3 characters.";
    }
    setErrors(formErrors);
    return Object.keys(formErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      if (!editId) {
        alert("State added successfully!");
        dispatch(addTask(formData)); // Add new state
      } else {
        alert("State updated successfully!");
        dispatch(editTask({ id: editId, ...formData })); // Update state
        setEditId(null); // Reset edit mode
      }
      setFormData({ country: "", stateName: "" });
    }
  };

  const handleDelete = (id) => {
    if (window.confirm("Are you sure you want to delete this state?")) {
      dispatch(deleteTask(id));
    }
  };

  const handleEdit = (id) => {
    const stateToEdit = tasks.find((task) => task.id === id);
    if (stateToEdit) {
      setFormData({ country: stateToEdit.country, stateName: stateToEdit.stateName });
      setEditId(id);
    }
  };

  useEffect(() => {
    // Simulate fetching states from an external source
    const fetchStates = async () => {
      try {
        // Placeholder: Replace with real fetch API if necessary
        const mockData = tasks; // Assuming tasks are preloaded
        dispatch(loadTasks(mockData));
      } catch (error) {
        console.error("Error fetching states:", error);
      }
    };
    fetchStates();
  }, [dispatch]);

  return (
    <div className="bg-gray-100 flex items-center justify-center h-screen">
      <div className="bg-white p-6 rounded shadow-md w-96">
        <button
          onClick={() => navigate("/")}
          className="rounded border-gray-300 bg-gray-500 text-white p-2 mb-4"
        >
          Back
        </button>
        <h2 className="text-2xl font-bold mb-4">Add/Update State</h2>
        <form id="stateForm" onSubmit={handleSubmit}>
          <div className="mb-4">
            <label
              htmlFor="country"
              className="block text-sm font-medium text-gray-700"
            >
              Select Country
            </label>
            <input
              id="country"
              value={formData.country}
              onChange={handleInputChange}
              className="mt-1 block w-full p-2 border border-gray-300 rounded"
            />
            {errors.country && <p className="text-red-500 text-sm">{errors.country}</p>}
          </div>
          <div className="mb-4">
            <label
              htmlFor="stateName"
              className="block text-sm font-medium text-gray-700"
            >
              State Name
            </label>
            <input
              type="text"
              id="stateName"
              value={formData.stateName}
              onChange={handleInputChange}
              className="mt-1 block w-full p-2 border border-gray-300 rounded"
            />
            {errors.stateName && <p className="text-red-500 text-sm">{errors.stateName}</p>}
          </div>
          <button
            type="submit"
            className="bg-blue-500 text-white font-bold py-2 px-4 rounded hover:bg-blue-600"
          >
            {editId ? "Update State" : "Add State"}
          </button>
        </form>

        <h2 className="text-xl font-bold mt-6 mb-4">States List</h2>
        <table className="min-w-full bg-white border border-gray-300">
          <thead>
            <tr>
              <th className="py-2 px-2 border-b text-left">ID</th>
              <th className="py-2 px-2 border-b text-left">Country</th>
              <th className="py-2 px-2 border-b text-left">State Name</th>
              <th className="py-2 px-2 border-b text-left">Edit</th>
              <th className="py-2 px-2 border-b text-left">Delete</th>
            </tr>
          </thead>
          <tbody>
            {tasks.map((value, index) => (
              <tr key={index}>
                <td className="py-1 px-2">{value.id}</td>
                <td className="py-1 px-2">{value.country}</td>
                <td className="py-1 px-2">{value.stateName}</td>
                <td className="py-1 px-2">
                  <button
                    onClick={() => handleEdit(value.id)}
                    className="text-blue-500"
                  >
                    Edit
                  </button>
                </td>
                <td className="py-1 px-2">
                  <button
                    onClick={() => handleDelete(value.id)}
                    className="text-red-500"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default State;
