import { createSlice } from "@reduxjs/toolkit";

// Predefined initial state
const initialState = {
  tasks: [
    { id: 1, name: 'siva', organization: 'xyz', email: 'x@gmail.com', password: '123456', numUsers: '1', mobile: '9999988888', country: 'India', state: 'TN', expireDays: '1', photo: null },
    { id: 2, name: 'saran', organization: 'abc', email: 'y@gmail.com', password: '654321', numUsers: '1', mobile: '9898989898', country: 'India', state: 'TN', expireDays: '1', photo: null },
  ], // Example states
};

const registerSlice = createSlice({
  name: "register",
  initialState,
  reducers: {
    // Add a new task
    addTask: (state, action) => {
      const newId = state.tasks.length > 0 ? Math.max(...state.tasks.map(task => task.id)) + 1 : 1;
      state.tasks.push({ ...action.payload, id: newId });
    },
    
    // Edit an existing task
    editTask: (state, action) => {
      const index = state.tasks.findIndex(task => task.id === action.payload.id);
      if (index !== -1) {
        state.tasks[index] = { ...action.payload }; // Update task data
      }
    },

    // Delete a task
    deleteTask: (state, action) => {
      state.tasks = state.tasks.filter(task => task.id !== action.payload);
    },
  },
});

export const { addTask, editTask, deleteTask } = registerSlice.actions;
export default registerSlice.reducer;
