import { configureStore } from "@reduxjs/toolkit";
import authReducer from "./authSlice"; // Adjust path if needed
import countryReducer from "./redux/countrySlice"; // Adjust path if needed

const store = configureStore({
  reducer: {
    auth: authReducer, // Register the `auth` reducer
    country: countryReducer, // Register the `country` reducer
  },
});

export default store;
