import { createSlice } from "@reduxjs/toolkit";
const initialState = {
  tasks: [
    { id: 1, countryCode: "IN", countryName: "India" },
    { id: 2, countryCode: "US", countryName: "United States" },
  ]  };

const countrySlice = createSlice({
  name: "country",
  initialState,
  reducers: {
    addTask: (state, action) => {
      const newId = state.tasks.length > 0 ? Math.max(...state.tasks.map((task) => task.id)) + 1 : 1;
      state.tasks.push({ id: newId, ...action.payload })},
    editTask: (state, action) => {
      const index = state.tasks.findIndex((task) => task.id === action.payload.id);
      if (index >= 0) { state.tasks[index] = { ...state.tasks[index], ...action.payload }}},
    deleteTask: (state, action) => { state.tasks = state.tasks.filter((task) => task.id !== action.payload)},
    loadTasks: (state, action) => { state.tasks = action.payload; // Replace tasks array with loaded data
    },},});

export const { addTask, editTask, deleteTask, loadTasks } = countrySlice.actions;
export default countrySlice.reducer;
