import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { addTask, editTask, deleteTask } from "../redux/countrySlice";

function Country() {
  const [formdata, setFormdata] = useState({ countryCode: "", countryName: "" });
  const [editId, setEditId] = useState(null);
  const dispatch = useDispatch();
  const tasks = useSelector((state) => state.country.tasks); // Access Redux state

  const handleChange = (e) => {
    setFormdata({ ...formdata, [e.target.id]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (editId !== null) {
      dispatch(editTask({ id: editId, ...formdata })); // Edit task
      setEditId(null);
    } else {
      const newId = tasks.length > 0 ? Math.max(...tasks.map((t) => t.id)) + 1 : 1;
      dispatch(addTask({ id: newId, ...formdata })); // Add task
    }
    setFormdata({ countryCode: "", countryName: "" });
  };

  const handleDelete = (id) => dispatch(deleteTask(id)); // Delete task

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input id="countryCode" value={formdata.countryCode} onChange={handleChange} />
        <input id="countryName" value={formdata.countryName} onChange={handleChange} />
        <button type="submit">{editId ? "Update" : "Add"}</button>
      </form>
      <ul>
        {tasks.map((task) => (
          <li key={task.id}>
            {task.countryCode} - {task.countryName}
            <button onClick={() => setEditId(task.id)}>Edit</button>
            <button onClick={() => handleDelete(task.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Country;
