import { createSlice } from "@reduxjs/toolkit";

// Predefined initial state
const initialState = {
  tasks: [
    { id: 1, statename:"TN", countryName: "India" },
    { id: 2, statename:"LG", countryName: "United States" },
  ], // Stores the list of countries
};

const stateSlice = createSlice({
  name: "state",
  initialState,
  reducers: {
    // Add a new country
    addTask: (state, action) => {
      const newId =
        state.tasks.length > 0
          ? Math.max(...state.tasks.map((task) => task.id)) + 1
          : 1; // Find max ID and increment
      state.tasks.push({ id: newId, ...action.payload });
    },
    // Edit an existing country
    editTask: (state, action) => {
      const index = state.tasks.findIndex((task) => task.id === action.payload.id);
      if (index >= 0) {
        state.tasks[index] = { ...state.tasks[index], ...action.payload }; // Merge updates
      }
    },
    // Delete a country
    deleteTask: (state, action) => {
      state.tasks = state.tasks.filter((task) => task.id !== action.payload);
    },
    // Load all countries
    loadTasks: (state, action) => {
      state.tasks = action.payload; // Replace tasks array with loaded data
    },
  },
});

export const { addTask, editTask, deleteTask, loadTasks } = countrySlice.actions;
export default countrySlice.reducer;
