import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import { addTask, editTask, deleteTask } from "../redux/registerSlice";

function Register() {
    const dispatch = useDispatch();
    const navigate=useNavigate();
    const [formData, setFormData] = useState({
        name: '', organization: '', email: '', password: '', numUsers: '', mobile: '', country: '', state: '', expireDays: '', photo: null
    });
    const [errors, setErrors] = useState({});
    const [editId, setEditId] = useState(null);
    const [isEditing, setIsEditing] = useState(false);

    // Fetch data from Redux store
    const countryList = useSelector((state) => state.country.tasks);
    const statelist = useSelector((state) => state.state.tasks);
    const displayData = useSelector((state) => state.register.tasks); // Fetch tasks from register slice

    useEffect(() => {
        setDisplayList(displayData);  // Sync displayList with the Redux store
    }, [displayData]); // Update displayList whenever displayData changes

    const handleInputChange = ({ target: { id, value, files } }) => {
        if (id === 'fil' && files && files.length > 0) {
            setFormData({ ...formData, photo: files[0] });
        } else {
            setFormData({ ...formData, [id]: value });
        }
        setErrors({ ...errors, [id]: '' });
    };

    const validateForm = () => {
        const formErrors = {};
        if (!formData.name) formErrors.name = "Required";
        if (!formData.organization) formErrors.organization = "Required";
        if (!formData.email) formErrors.email = !/\S+@\S+\.\S+/.test(formData.email) ? "Invalid Email" : "Required";
        if (!formData.password || formData.password.length < 6) formErrors.password = "Min 6 chars";
        if (!formData.numUsers || isNaN(formData.numUsers) || formData.numUsers <= 0) formErrors.numUsers = "Invalid number";
        if (!formData.mobile || !/^\d{10}$/.test(formData.mobile)) formErrors.mobile = "Invalid mobile";
        if (!formData.country) formErrors.country = "Required";
        if (!formData.state) formErrors.state = "Required";
        if (!formData.expireDays || isNaN(formData.expireDays) || formData.expireDays <= 0) formErrors.expireDays = "Invalid days";

        setErrors(formErrors);
        return !Object.keys(formErrors).length;
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (validateForm()) {
            if (!editId) {
                dispatch(addTask(formData));  // Add new task
            } else {
                dispatch(editTask({ ...formData, id: editId }));  // Edit existing task
            }
            setFormData({
                name: '', organization: '', email: '', password: '', numUsers: '', mobile: '',
                country: '', state: '', expireDays: '', photo: null
            });
            setEditId(null);  // Reset edit mode
        }
    };

    const handleDelete = (id) => {
        dispatch(deleteTask(id));  // Delete task by id
    };

    const handleEdit = (id) => {
        const taskToEdit = displayData.find((task) => task.id === id);
        setFormData(taskToEdit);  // Set form data to the task being edited
        setEditId(id);  // Set the edit mode to true
    };

    return (
        <div className="bg-gray-100 flex flex-col items-center">
            <div className="text-center mb-5">
                <h1 className="text-3xl font-bold mb-5">Registered Users</h1>
                <div className="flex justify-center gap-4 flex-wrap">
                    <button onClick={() => { navigate('/country') }} className="bg-blue-500 text-white py-2 px-4 rounded">Add Country</button>
                    <button onClick={() => { navigate('/state') }} className="bg-green-500 text-white py-2 px-4 rounded">Add State</button>
                </div>
            </div>

            <form onSubmit={handleSubmit} className="bg-white p-6 rounded-lg shadow-md">
                {/* Form fields */}
                <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                    <div>
                        <label htmlFor="name">User Name</label>
                        <input id="name" type="text" value={formData.name} onChange={handleInputChange} />
                        {errors.name && <p className="text-red-500">{errors.name}</p>}
                    </div>
                    {/* Other input fields */}
                    <div>
                        <label htmlFor="country">Select Country</label>
                        <select id="country" value={formData.country} onChange={handleInputChange}>
                            <option value="">Choose Country</option>
                            {countryList.map((country, i) => (
                                <option key={i} value={country.countryname}>{country.countryname}</option>
                            ))}
                        </select>
                        {errors.country && <p className="text-red-500">{errors.country}</p>}
                    </div>
                    <div>
                        <label htmlFor="state">Select State</label>
                        <select id="state" value={formData.state} onChange={handleInputChange}>
                            <option value="">Choose State</option>
                            {statelist.map((state, i) => (
                                <option key={i} value={state.statename}>{state.statename}</option>
                            ))}
                        </select>
                        {errors.state && <p className="text-red-500">{errors.state}</p>}
                    </div>
                </div>

                <button type="submit">{isEditing ? "Update" : "Submit"}</button>
            </form>

            <h2 className="text-center text-3xl mb-4">User Details</h2>
            <table className="w-full">
                <thead>
                    <tr>
                        <th>Organization</th>
                        <th>Photo</th>
                        <th>Name</th>
                        <th>Mobile No</th>
                        <th>Email</th>
                        <th>No of Users</th>
                        <th>State</th>
                        <th>Country</th>
                        <th>Expire Date</th>
                        <th>Edit</th>
                        <th>Delete</th>
                    </tr>
                </thead>
                <tbody>
                    {displayData.map((user) => (
                        <tr key={user.id}>
                            <td>{user.organization || "N/A"}</td>
                            <td>{user.photo ? <img src={URL.createObjectURL(user.photo)} alt="User" /> : "No Photo"}</td>
                            <td>{user.name || "N/A"}</td>
                            <td>{user.mobile || "N/A"}</td>
                            <td>{user.email || "N/A"}</td>
                            <td>{user.numUsers || "N/A"}</td>
                            <td>{user.state || "N/A"}</td>
                            <td>{user.country || "N/A"}</td>
                            <td>{user.expireDays || "N/A"}</td>
                            <td><button onClick={() => handleEdit(user.id)}>Edit</button></td>
                            <td><button onClick={() => handleDelete(user.id)}>Delete</button></td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default Register;
