import React, { useState, useEffect } from 'react';

function CountryStateComponent() {
  const [countries, setCountries] = useState([]);
  const [states, setStates] = useState([]);  // Initially set to an empty array
  const [selectedCountry, setSelectedCountry] = useState('');

  // Fetch countries when component mounts
  useEffect(() => {
    fetch('http://localhost:5000/countries')
      .then(response => response.json())
      .then(data => setCountries(data))
      .catch(error => console.error('Error fetching countries:', error));
  }, []);

  // Fetch states when selected country changes
  useEffect(() => {
    if (selectedCountry) {
      console.log('Selected Country:', selectedCountry);  // Log selected country
      fetch(`http://localhost:5000/register/states/${selectedCountry}`)
        .then(response => response.json())
        .then(data => {
          if (Array.isArray(data)) {
            setStates(data);  // Only set states if the response is an array
          } else {
            console.error('Invalid data format for states:', data);
          }
        })
        .catch(error => console.error('Error fetching states:', error));
    }
  }, [selectedCountry]);

  // Handle country selection
  const handleCountryChange = (event) => {
    setSelectedCountry(event.target.value);
  };

  return (
    <div>
      <h1>Select Country</h1>
      <select onChange={handleCountryChange} value={selectedCountry}>
        <option value="">--Select Country--</option>
        {countries.map((country, index) => (
          <option key={index} value={country.country_name}>
            {country.country_name}
          </option>
        ))}
      </select>

      <h2>States:</h2>
      <ul>
        {Array.isArray(states) && states.length > 0 ? (
          states.map((state, index) => (
            <li key={index}>{state.state_name}</li>
          ))
        ) : (
          <li>No states available or loading...</li>
        )}
      </ul>
    </div>
  );
}

export default CountryStateComponent;
