import React, { useState, useEffect } from 'react';

const Register = () => {
  const [countries, setCountries] = useState([]);
  const [states, setStates] = useState([]);
  const [selectedCountry, setSelectedCountry] = useState('');
  const [formData, setFormData] = useState({
    name: '',
    organization: '',
    email: '',
    password: '',
    numUsers: '',
    mobile: '',
    country: '',
    state: '',
    expireDays: '',
    fil: null,
  });
  const [errors, setErrors] = useState({});
  const [registerData, setRegisterData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    fetch('http://localhost:5000/register')
      .then((response) => response.json())
      .then((data) => {
        console.log(data); // Log data to verify it's coming through
        setRegisterData(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
        setLoading(false);
      });
  }, []);

  useEffect(() => {
    // Fetch countries on component mount
    fetch('http://localhost:5000/register/countries')
      .then((response) => response.json())
      .then((data) => setCountries(data))
      .catch((error) => console.error('Error fetching countries:', error));
  }, []);

  useEffect(() => {
    if (selectedCountry) {
      // Fetch states based on selected country
      fetch(`http://localhost:5000/register/states/${selectedCountry}`)
        .then((response) => response.json())
        .then((data) => setStates(data))
        .catch((error) => console.error('Error fetching states:', error));
    }
  }, [selectedCountry]);

  // Handle input change
  const handleInputChange = (e) => {
    const { id, value } = e.target;
    setFormData({
      ...formData,
      [id]: value,
    });
  };

  const handleCountryChange = (e) => {
    setSelectedCountry(e.target.value);
    setFormData({
      ...formData,
      country: e.target.value,
    });
  };

  const handleFileChange = (e) => {
    setFormData({
      ...formData,
      fil: e.target.files[0],
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Perform validation
    if (validateForm()) {
      // Send form data to the backend
      console.log('Form submitted:', formData);
    }
  };

  const validateForm = () => {
    let formErrors = {};
    if (!formData.name) formErrors.name = 'Name is required';
    if (!formData.email) formErrors.email = 'Email is required';
    if (!formData.password) formErrors.password = 'Password is required';
    if (!formData.numUsers) formErrors.numUsers = 'Number of users is required';
    if (!formData.mobile) formErrors.mobile = 'Mobile is required';
    if (!formData.country) formErrors.country = 'Country is required';
    if (!formData.state) formErrors.state = 'State is required';
    if (!formData.expireDays) formErrors.expireDays = 'Expire days is required';

    setErrors(formErrors);
    return Object.keys(formErrors).length === 0;
  };

  const handleEdit = (id) => {
    console.log('Editing user with id:', id);
    // Edit logic (you can fetch the specific user data to edit)
  };

  const handleDelete = (id) => {
    console.log('Deleting user with id:', id);
    // Delete logic (you can call an API to delete the user)
  };

  return (
    <div>
      <h2>Register</h2>
      <div>
        <label>Country: </label>
        <select onChange={handleCountryChange} value={formData.country}>
          <option value="">Select a Country</option>
          {countries.map((country, index) => (
            <option key={index} value={country.country_name}>
              {country.country_name}
            </option>
          ))}
        </select>
      </div>

      {formData.country && (
        <div>
          <label>State: </label>
          <select
            value={formData.state}
            onChange={handleInputChange}
            id="state"
          >
            <option value="">Select a State</option>
            {states.map((state, index) => (
              <option key={index} value={state.state_name}>
                {state.state_name}
              </option>
            ))}
          </select>
        </div>
      )}

      <h2>Register Data</h2>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <table className="table-auto border-collapse w-full">
          <thead>
            <tr>
              <th className="border px-4 py-2">ID</th>
              <th className="border px-4 py-2">Organization</th>
              <th className="border px-4 py-2">Photo</th>
              <th className="border px-4 py-2">Name</th>
              <th className="border px-4 py-2">Mobile</th>
              <th className="border px-4 py-2">Email</th>
              <th className="border px-4 py-2">Password</th>
              <th className="border px-4 py-2">Num Users</th>
              <th className="border px-4 py-2">State</th>
              <th className="border px-4 py-2">Country</th>
              <th className="border px-4 py-2">Expiry Date</th>
              <th className="border px-4 py-2">Edit</th>
              <th className="border px-4 py-2">Delete</th>
            </tr>
          </thead>
          <tbody>
            {registerData.map((user, index) => (
              <tr key={index}>
                <td className="border px-4 py-2">{user.id || "N/A"}</td>
                <td className="border px-4 py-2">{user.organization || "N/A"}</td>
                <td className="border px-4 py-2">
                  {user.photo ? (
                    <img
                      src={URL.createObjectURL(user.photo)} // Assuming the photo is in binary format
                      alt="User Photo"
                      className="w-12 h-12 rounded-full"
                    />
                  ) : (
                    "No Photo"
                  )}
                </td>
                <td className="border px-4 py-2">{user.name || "N/A"}</td>
                <td className="border px-4 py-2">{user.mobile || "N/A"}</td>
                <td className="border px-4 py-2">{user.email || "N/A"}</td>
                <td className="border px-4 py-2">{user.password || "N/A"}</td>
                <td className="border px-4 py-2">{user.num_users || "N/A"}</td>
                <td className="border px-4 py-2">{user.state_name || "N/A"}</td>
                <td className="border px-4 py-2">{user.country_name || "N/A"}</td>
                <td className="border px-4 py-2">{user.expiry_date || "N/A"}</td>
                <td className="border px-4 py-2">
                  <button onClick={() => handleEdit(user.id)}>Edit</button>
                </td>
                <td className="border px-4 py-2">
                  <button onClick={() => handleDelete(user.id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      <div className="bg-gray-100 flex flex-col items-center">
        <div className="text-center mb-5">
          <h1 className="text-3xl font-bold mb-5">Register New User</h1>
          <form onSubmit={handleSubmit} className="bg-white p-6 rounded-lg shadow-md">
            <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
              {/* Input fields for user details */}
              <div className="flex flex-col">
                <label htmlFor="name">Name</label>
                <input
                  id="name"
                  type="text"
                  value={formData.name}
                  onChange={handleInputChange}
                  className="border p-2"
                />
                {errors.name && <span className="text-red-500 text-sm">{errors.name}</span>}
              </div>
              <div className="flex flex-col">
                <label htmlFor="organization">Organization</label>
                <input
                  id="organization"
                  type="text"
                  value={formData.organization}
                  onChange={handleInputChange}
                  className="border p-2"
                />
              </div>
              {/* Other form fields... */}
              <div className="flex flex-col">
                <label htmlFor="email">Email</label>
                <input
                  id="email"
                  type="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  className="border p-2"
                />
                {errors.email && <span className="text-red-500 text-sm">{errors.email}</span>}
              </div>
              <div className="flex flex-col">
                <label htmlFor="password">Password</label>
                <input
                  id="password"
                  type="password"
                  value={formData.password}
                  onChange={handleInputChange}
                  className="border p-2"
                />
                {errors.password && <span className="text-red-500 text-sm">{errors.password}</span>}
              </div>
              <div className="flex flex-col">
                <label htmlFor="numUsers">Number of Users</label>
                <input
                  id="numUsers"
                  type="text"
                  value={formData.numUsers}
                  onChange={handleInputChange}
                  className="border p-2"
                />
                {errors.numUsers && <span className="text-red-500 text-sm">{errors.numUsers}</span>}
              </div>
              <div className="flex flex-col">
                <label htmlFor="mobile">Mobile</label>
                <input
                  id="mobile"
                  type="text"
                  value={formData.mobile}
                  onChange={handleInputChange}
                  className="border p-2"
                />
                {errors.mobile && <span className="text-red-500 text-sm">{errors.mobile}</span>}
              </div>
              <div className="flex flex-col">
                <label htmlFor="expireDays">Expire Days</label>
                <input
                  id="expireDays"
                  type="text"
                  value={formData.expireDays}
                  onChange={handleInputChange}
                  className="border p-2"
                />
                {errors.expireDays && <span className="text-red-500 text-sm">{errors.expireDays}</span>}
              </div>
            </div>
            <button type="submit" className="bg-blue-500 text-white p-2 mt-4 rounded-lg">
              Submit
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Register;
