const handleSubmit = async () => {
  const formData = { country_name: 'United States', state_name: 'California' };
  
  try {
    const response = await fetch('http://localhost:5000/states', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData),
    });

    const responseBody = await response.json();  // Capture the response body
    
    if (response.ok) {
      console.log('Data added successfully', responseBody);
    } else {
      console.error('Error adding data:', responseBody);
    }
  } catch (error) {
    console.error('Error:', error);
  }
};
