import { useCallback, useEffect, useState } from "react";
import { NavLink, useNavigate } from 'react-router-dom';

function Register() {
    const navigate = useNavigate();

    const [formData, setFormData] = useState({
        name: '', organization: '', email: '', password: '', numUsers: '',
        mobile: '', country: '', state: '', expireDays: '', photo: null
    });
    const [errors, setErrors] = useState({});
    const [displayList, setDisplayList] = useState([]);
    const [editId, setEditId] = useState(null);
    const [isEditing, setIsEditing] = useState(false);
    const [countries, setCountries] = useState([]);
    const [states, setStates] = useState([]);

    useEffect(() => {
        const fetchCountries = async () => {
            const response = await fetch('/api/countries');
            const data = await response.json();
            setCountries(data);
        };
        fetchCountries();
    }, []);

    const handleCountryChange = async (e) => {
        const selectedCountry = e.target.value;
        setFormData({ ...formData, country: selectedCountry });
        
        const response = await fetch(`/api/states/${selectedCountry}`);
        const data = await response.json();
        setStates(data);
    };

    const handleInputChange = ({ target: { id, value } }) => {
        setFormData({ ...formData, [id]: value });
        setErrors({ ...errors, [id]: '' })};

    const validateForm = () => {
        const formErrors = {};
        if (!formData.name) formErrors.name = "Required";
        if (!formData.organization) formErrors.organization = "Required";
        if (!formData.email) formErrors.email = !/\S+@\S+\.\S+/.test(formData.email) ? "Invalid Email" : "Required";
        if (!formData.password || formData.password.length < 6) formErrors.password = "Min 6 chars";
        if (!formData.numUsers || isNaN(formData.numUsers) || formData.numUsers <= 0) formErrors.numUsers = "Invalid number";
        if (!formData.mobile || !/^\d{10}$/.test(formData.mobile)) formErrors.mobile = "Invalid mobile";
        if (!formData.country) formErrors.country = "Required";
        if (!formData.state) formErrors.state = "Required";
        if (!formData.expireDays || isNaN(formData.expireDays) || formData.expireDays <= 0) formErrors.expireDays = "Invalid days";

        setErrors(formErrors);
        return !Object.keys(formErrors).length;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (validateForm()) {
            const formPayload = new FormData();
            Object.keys(formData).forEach(key => formPayload.append(key, formData[key]));

            const method = editId ? 'PUT' : 'POST';
            const url = editId ? `/api/users/${editId}` : '/api/users';

            try {
                const response = await fetch(url, {
                    method,
                    body: formPayload,
                });
                if (response.ok) {
                    alert(editId ? "Form updated successfully!" : "Form submitted successfully!");
                    setFormData({ name: '', organization: '', email: '', password: '', numUsers: '', mobile: '', country: '', state: '', expireDays: '', photo: null });
                    document.getElementById("fil").value = "";
                    setEditId(null);
                    fetchData();
                } else {
                    console.error("Failed to submit form data.");
                }
            } catch (error) {
                console.error("Error:", error);
            }
        }
    };

    const fetchData = useCallback(async () => {
        try {
            const response = await fetch('/api/users');
            if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
            const data = await response.json();
            setDisplayList(data);
        } catch (error) {
            console.error('Fetch error:', error);
        }
    }, []);
    
    useEffect(() => {
        fetchData();
    }, [fetchData]);

    const handleEdit = (id) => {
        const user = displayList.find(user => user.id === id);
        setFormData(user);
        setEditId(id);
        setIsEditing(true);
    };

    const handleDelete = async (id) => {
        if (window.confirm("Are you sure you want to delete this user?")) {
            try {
                const response = await fetch(`/api/users/${id}`, { method: 'DELETE' });
                if (response.ok) {
                    alert("User deleted successfully");
                    fetchData();
                } else {
                    console.error("Failed to delete user.");
                }
            } catch (error) {
                console.error("Error:", error);
            }
        }
    };
    return (
        <div className="bg-gray-100 flex flex-col items-center">
            <div className="text-center mb-5">
                <h1 className="text-3xl font-bold mb-5">Registered Users (Router)</h1>
                <div className="flex justify-center gap-4 flex-wrap">
                    <NavLink to="/country" className="bg-blue-500 text-white font-bold py-2 px-4 rounded hover:bg-blue-600 mb-4">
                        Add Country
                    </NavLink>
                    <button onClick={() => { navigate('/state') }} className="bg-green-500 text-white font-bold py-2 px-4 rounded hover:bg-green-600 mb-4">
                        Add State
                    </button>
                </div>
            </div>
    
            <div className="flex flex-col space-y-4 w-full max-w-4xl mx-auto p-4">
                <form onSubmit={handleSubmit} className="bg-white p-6 rounded-lg shadow-md">
                    <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                        {/* Fields go here (e.g., name, organization, email, etc.) */}
                        <div className="flex flex-col">
                            <label htmlFor="name">Name</label>
                            <input
                                id="name"
                                type="text"
                                value={formData.name}
                                onChange={handleInputChange}
                                className="border p-2"
                            />
                            {errors.name && <span className="text-red-500 text-sm">{errors.name}</span>}
                        </div>
                        <div className="flex flex-col">
                            <label htmlFor="organization">Organization</label>
                            <input
                                id="organization"
                                type="text"
                                value={formData.organization}
                                onChange={handleInputChange}
                                className="border p-2"
                            />
                            {errors.organization && <span className="text-red-500 text-sm">{errors.organization}</span>}
                        </div>
                        <div className="flex flex-col">
                            <label htmlFor="email">Email</label>
                            <input
                                id="email"
                                type="email"
                                value={formData.email}
                                onChange={handleInputChange}
                                className="border p-2"
                            />
                            {errors.email && <span className="text-red-500 text-sm">{errors.email}</span>}
                        </div>
                        <div className="flex flex-col">
                            <label htmlFor="password">Password</label>
                            <input
                                id="password"
                                type="password"
                                value={formData.password}
                                onChange={handleInputChange}
                                className="border p-2"
                            />
                            {errors.password && <span className="text-red-500 text-sm">{errors.password}</span>}
                        </div>
                        <div className="flex flex-col">
                            <label htmlFor="numUsers">Number of Users</label>
                            <input
                                id="numUsers"
                                type="number"
                                value={formData.numUsers}
                                onChange={handleInputChange}
                                className="border p-2"
                            />
                            {errors.numUsers && <span className="text-red-500 text-sm">{errors.numUsers}</span>}
                        </div>
                        <div className="flex flex-col">
                            <label htmlFor="mobile">Mobile</label>
                            <input
                                id="mobile"
                                type="text"
                                value={formData.mobile}
                                onChange={handleInputChange}
                                className="border p-2"
                            />
                            {errors.mobile && <span className="text-red-500 text-sm">{errors.mobile}</span>}
                        </div>
                        <div className="flex flex-col">
                            <label htmlFor="country">Country</label>
                            <select
                                id="country"
                                value={formData.country}
                                onChange={handleCountryChange}
                                className="border p-2"
                            >
                                <option value="">Select Country</option>
                                {countries.map((country) => (
                                    <option key={country.id} value={country.id}>{country.name}</option>
                                ))}
                            </select>
                            {errors.country && <span className="text-red-500 text-sm">{errors.country}</span>}
                        </div>
                        <div className="flex flex-col">
                            <label htmlFor="state">State</label>
                            <select
                                id="state"
                                value={formData.state}
                                onChange={handleInputChange}
                                className="border p-2"
                            >
                                <option value="">Select State</option>
                                {states.map((state) => (
                                    <option key={state.id} value={state.id}>{state.name}</option>
                                ))}
                            </select>
                            {errors.state && <span className="text-red-500 text-sm">{errors.state}</span>}
                        </div>
                        <div className="flex flex-col">
                            <label htmlFor="expireDays">Expire Days</label>
                            <input
                                id="expireDays"
                                type="number"
                                value={formData.expireDays}
                                onChange={handleInputChange}
                                className="border p-2"
                            />
                            {errors.expireDays && <span className="text-red-500 text-sm">{errors.expireDays}</span>}
                        </div>
                        <div className="flex flex-col">
                            <label htmlFor="fil">Upload Photo</label>
                            <input
                                id="fil"
                                type="file"
                                onChange={handleInputChange}
                                className="border p-2"
                            />
                        </div>
                    </div>
                    <button type="submit" className="border border-green-200 text-black bg-purple-500 m-3 p-3 rounded-md w-full">
                        {isEditing ? "Update" : "Submit"}
                    </button>
                    <button type="button" onClick={fetchData} className="border border-grey-200 text-black bg-green-500 m-3 p-3 rounded-md w-full">
                        Load Records
                    </button>
                </form>
            </div>
    
            <h2 className="text-black text-center text-3xl mb-4">User Details</h2>
            <table className="table-auto border border-gray-200 bg-white w-full">
                <thead>
                    <tr>
                    <th className="border-2 border-green-300 px-4 py-2">ID</th>
                        <th className="border-2 border-green-300 px-4 py-2">Organization</th>
                        <th className="border-2 border-green-300 px-4 py-2">Photo</th>
                        <th className="border-2 border-green-300 px-4 py-2">Name</th>
                        <th className="border-2 border-green-300 px-4 py-2">Mobile No</th>
                        <th className="border-2 border-green-300 px-4 py-2">Email</th>
                        <th className="border-2 border-green-300 px-4 py-2">Password</th>

                        <th className="border-2 border-green-300 px-4 py-2">No of Users</th>
                        <th className="border-2 border-green-300 px-4 py-2">State</th>
                        <th className="border-2 border-green-300 px-4 py-2">Country</th>
                        <th className="border-2 border-green-300 px-4 py-2">Expire Date</th>
                        <th className="border-2 border-green-300 px-4 py-2">Edit</th>
                        <th className="border-2 border-green-300 px-4 py-2">Delete</th>
                    </tr>
                </thead>
                <tbody>
                    {displayList.length === 0 ? (
                        <tr><td colSpan="11" className="text-center">No records found.</td></tr>
                    ) : (
                        displayList.map((user, index) => (
                            <tr key={index}>
                                <td>{user.id || "N/A"}</td>
                                <td>{user.organization || "N/A"}</td>
                                <td>{user.photo ? <img src={URL.createObjectURL(user.photo)} alt="User Photo" className="w-12 h-12 rounded-full" /> : "No Photo"}</td>
                                <td>{user.name || "N/A"}</td>
                                <td>{user.mobile || "N/A"}</td>
                                <td>{user.email || "N/A"}</td>
                                <td>{user.password || "N/A"}</td>                               
                                <td>{user.numUsers || "N/A"}</td>
                                <td>{user.state || "N/A"}</td>
                                <td>{user.country || "N/A"}</td>
                                <td>{user.expireDays || "N/A"}</td>
                                <td onClick={() => handleEdit(user.id)}><button>Edit</button></td>
                                <td onClick={() => handleDelete(user.id)}><button>Delete</button></td>
                            </tr>
                        ))
                    )}
                </tbody>
            </table>
        </div>
    )}
