import React, { useState, useEffect } from 'react';

const Register = () => {
    const [countries, setCountries] = useState([]);
    const [states, setStates] = useState([]); // Default to an empty array
    const [selectedCountry, setSelectedCountry] = useState('');
    const [selectedState, setSelectedState] = useState('');

    useEffect(() => {
        // Fetch the list of countries on component mount
        const fetchCountries = async () => {
            try { const response = await fetch('http://localhost:5000/countries');
                const data = await response.json();
                setCountries(data);
            } catch (err) { console.error('Error fetching countries:', err);
            } }; fetchCountries(); }, []);

    const handleCountryChange = (e) => {
        setSelectedCountry(e.target.value);
        setSelectedState(''); // Reset state selection when country changes
    };

    const handleStateChange = (e) => {
        setSelectedState(e.target.value);
    };

    return (
        <div>
            <h1>Select Country and State</h1>
            <div>
                <label>Country: </label>
                <select value={selectedCountry} onChange={handleCountryChange}>
                    <option value="">Select a Country</option>
                    {countries.map((country, index) => (
                        <option key={index} value={country.country_name}>
                            {country.country_name}
                        </option>
                    ))}
                </select>
            </div>

            {/* State Dropdown */}
            <div>
                <label>State: </label>
                <select value={selectedState} onChange={handleStateChange} disabled={!selectedCountry}>
                    <option value="">Select a State</option>
                    {states.map((state, index) => (
                        <option key={index} value={state.state_name}>
                            {state.state_name}
                        </option>
                    ))}
                </select>
            </div>

            {/* Display selected values */}
            <div>
                <p>Selected Country: {selectedCountry}</p>
                <p>Selected State: {selectedState}</p>
            </div>
        </div>
    );
};

export default Register;
