import React from 'react';

const Form2 = () => {
  const handleSubmit = async () => {
    // The data should probably be an object instead of an array
    const formData = { country_name: 'United States', state_name: 'California' };

    try {
      const response = await fetch('http://localhost:5000/states', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        console.log('Data added successfully');
      } else {
        console.error('Error adding data');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return <button onClick={handleSubmit}>Add Data</button>;
};

export default Form2;
