import { useCallback, useEffect, useState } from "react";
import { NavLink, useNavigate } from 'react-router-dom';

function Register() {
    const navigate = useNavigate();

    const [formData, setFormData] = useState({
        name: '', organization: '', email: '', password: '', numUsers: '',
        mobile: '', country: '', state: '', expireDays: '', photo: null, 
        address: '', jobTitle: '' // New fields added
    });
    const [errors, setErrors] = useState({});
    const [displayList, setDisplayList] = useState([]);
    const [editId, setEditId] = useState(null);
    const [isEditing, setIsEditing] = useState(false);
    const [countries, setCountries] = useState([]);
    const [states, setStates] = useState([]);

    useEffect(() => {
        const fetchCountries = async () => {
            const response = await fetch('/api/countries');
            const data = await response.json();
            setCountries(data);
        };
        fetchCountries();
    }, []);

    const handleCountryChange = async (e) => {
        const selectedCountry = e.target.value;
        setFormData({ ...formData, country: selectedCountry });
        
        const response = await fetch(`/api/states/${selectedCountry}`);
        const data = await response.json();
        setStates(data);
    };

    const handleInputChange = ({ target: { id, value, files } }) => {
        if (id === 'fil' && files && files.length > 0) {
            setFormData({ ...formData, photo: files[0] });
        } else {
            setFormData({ ...formData, [id]: value });
        }
        setErrors({ ...errors, [id]: '' });
    };

    const validateForm = () => {
        const formErrors = {};
        if (!formData.name) formErrors.name = "Required";
        if (!formData.organization) formErrors.organization = "Required";
        if (!formData.email) formErrors.email = !/\S+@\S+\.\S+/.test(formData.email) ? "Invalid Email" : "Required";
        if (!formData.password || formData.password.length < 6) formErrors.password = "Min 6 chars";
        if (!formData.numUsers || isNaN(formData.numUsers) || formData.numUsers <= 0) formErrors.numUsers = "Invalid number";
        if (!formData.mobile || !/^\d{10}$/.test(formData.mobile)) formErrors.mobile = "Invalid mobile";
        if (!formData.country) formErrors.country = "Required";
        if (!formData.state) formErrors.state = "Required";
        if (!formData.expireDays || isNaN(formData.expireDays) || formData.expireDays <= 0) formErrors.expireDays = "Invalid days";
        if (!formData.address) formErrors.address = "Required"; // Validation for the new field
        if (!formData.jobTitle) formErrors.jobTitle = "Required"; // Validation for the new field

        setErrors(formErrors);
        return !Object.keys(formErrors).length;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (validateForm()) {
            const formPayload = new FormData();
            Object.keys(formData).forEach(key => formPayload.append(key, formData[key]));

            const method = editId ? 'PUT' : 'POST';
            const url = editId ? `/api/users/${editId}` : '/api/users';

            try {
                const response = await fetch(url, {
                    method,
                    body: formPayload,
                });
                if (response.ok) {
                    alert(editId ? "Form updated successfully!" : "Form submitted successfully!");
                    setFormData({ name: '', organization: '', email: '', password: '', numUsers: '', mobile: '', country: '', state: '', expireDays: '', photo: null, address: '', jobTitle: '' });
                    document.getElementById("fil").value = "";
                    setEditId(null);
                    fetchData();
                } else {
                    console.error("Failed to submit form data.");
                }
            } catch (error) {
                console.error("Error:", error);
            }
        }
    };

    const fetchData = useCallback(async () => {
        try {
            const response = await fetch('/api/users');
            if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
            const data = await response.json();
            setDisplayList(data);
        } catch (error) {
            console.error('Fetch error:', error);
        }
    }, []);
    
    useEffect(() => {
        fetchData();
    }, [fetchData]);

    const handleEdit = (id) => {
        const user = displayList.find(user => user.id === id);
        setFormData(user);
        setEditId(id);
        setIsEditing(true);
    };

    const handleDelete = async (id) => {
        if (window.confirm("Are you sure you want to delete this user?")) {
            try {
                const response = await fetch(`/api/users/${id}`, { method: 'DELETE' });
                if (response.ok) {
                    alert("User deleted successfully");
                    fetchData();
                } else {
                    console.error("Failed to delete user.");
                }
            } catch (error) {
                console.error("Error:", error);
            }
        }
    };

    return (
        <div className="bg-gray-100 flex flex-col items-center">
            <div className="text-center mb-5">
                <h1 className="text-3xl font-bold mb-5">Registered Users (Router)</h1>
                <div className="flex justify-center gap-4 flex-wrap">
                    <NavLink to="/country" className="bg-blue-500 text-white font-bold py-2 px-4 rounded hover:bg-blue-600 mb-4">
                        Add Country
                    </NavLink>
                    <button onClick={() => { navigate('/state') }} className="bg-green-500 text-white font-bold py-2 px-4 rounded hover:bg-green-600 mb-4">
                        Add State
                    </button>
                </div>
            </div>
    
            <div className="flex flex-col space-y-4 w-full max-w-4xl mx-auto p-4">
                <form onSubmit={handleSubmit} className="bg-white p-6 rounded-lg shadow-md">
                    <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                        {/* Existing Fields */}
                        <div className="flex flex-col">
                            <label htmlFor="name">Name</label>
                            <input
                                id="name"
                                type="text"
                                value={formData.name}
                                onChange={handleInputChange}
                                className="border p-2"
                            />
                            {errors.name && <span className="text-red-500 text-sm">{errors.name}</span>}
                        </div>
                        {/* ... Other fields */}
                        <div className="flex flex-col">
                            <label htmlFor="address">Address</label>
                            <input
                                id="address"
                                type="text"
                                value={formData.address}
                                onChange={handleInputChange}
                                className="border p-2"
                            />
                            {errors.address && <span className="text-red-500 text-sm">{errors.address}</span>}
                        </div>
                        <div className="flex flex-col">
                            <label htmlFor="jobTitle">Job Title</label>
                            <input
                                id="jobTitle"
                                type="text"
                                value={formData.jobTitle}
                                onChange={handleInputChange}
                                className="border p-2"
                            />
                            {errors.jobTitle && <span className="text-red-500 text-sm">{errors.jobTitle}</span>}
                        </div>
                        {/* ... Remaining fields */}
                    </div>
                    <button type="submit" className="border border-green-200 text-black bg-purple-500 m-3 p-3 rounded-md w-full">
                        {isEditing ? "Update" : "Submit"}
                    </button>
                    <button type="button" onClick={fetchData} className="border border-grey-200 text-black bg-green-500 m-3 p-3 rounded-md w-full">
                        Load Records
                    </button>
                </form>
            </div>
    
            <h2 className="text-black text-center text-3xl mb-4">User Details</h2>
            <table className="table-auto border border-gray-200 bg-white w-full">
                <thead>
                    <tr>
                    <th className="border-2 border-green-300 px-4 py-2">ID</th>
                        {/* Existing columns */}
                        <th className="border-2 border-green-300 px-4 py-2">Address</th>
                        <th className="border-2 border-green-300 px-4 py-2">Job Title</th>
                        {/* Existing columns */}
                        <th className="border-2 border-green-300 px-4 py-2">Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {displayList.map(user => (
                        <tr key={user.id}>
                            <td className="border-2 px-4 py-2">{user.id}</td>
                            {/* Existing rows */}
                            <td className="border-2 px-4 py-2">{user.address}</td>
                            <td className="border-2 px-4 py-2">{user.jobTitle}</td>
                            {/* Existing rows */}
                            <td className="border-2 px-4 py-2">
                                <button onClick={() => handleEdit(user.id)} className="bg-blue-500 text-white px-4 py-2 rounded-md mr-2">
                                    Edit
                                </button>
                                <button onClick={() => handleDelete(user.id)} className="bg-red-500 text-white px-4 py-2 rounded-md">
                                    Delete
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default Register;
