import React, { useState, useEffect } from "react";

function State() {
  const [formData, setFormData] = useState({ country: '', stateName: '' });
  const [errors, setErrors] = useState({});
  const [displayData, setDisplayData] = useState([]);
  const [countryList, setCountryList] = useState([]);
  const [editId, setEditId] = useState(null);

  // Fetch list of countries when the component mounts
  useEffect(() => {
    fetch("http://localhost:5000/countries")
      .then(response => response.json())
      .then(data => setCountryList(data))
      .catch(error => console.error("Error fetching countries:", error));
  }, []);

  // Handle form data change
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  // Validate form data
  const validateForm = () => {
    let formErrors = {};
    if (!formData.country) formErrors.country = "Country is required.";
    if (!formData.stateName) formErrors.stateName = "State name is required.";
    return formErrors;
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    const formErrors = validateForm();
    if (Object.keys(formErrors).length > 0) {
      setErrors(formErrors);
      return;
    }
    
    try {
      const response = await fetch("http://localhost:5000/states", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({
          stateName: formData.stateName,
          countryName: formData.country
        })
      });
      if (response.status === 201) {
        // Add the new state to the display data
        const newState = await response.json();
        setDisplayData([...displayData, newState]);
        setFormData({ country: '', stateName: '' }); // Clear form
      } else {
        const errorData = await response.json();
        alert("Failed to add state: " + errorData.message);
      }
    } catch (error) {
      console.error("Error submitting state:", error);
      alert("Error submitting state.");
    }
  };

  return (
    <div>
      <h2>Add State</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Country</label>
          <select
            name="country"
            value={formData.country}
            onChange={handleChange}
          >
            <option value="">Select Country</option>
            {countryList.map((country) => (
              <option key={country.id} value={country.country_name}>
                {country.country_name}
              </option>
            ))}
          </select>
          {errors.country && <p style={{ color: "red" }}>{errors.country}</p>}
        </div>
        <div>
          <label>State Name</label>
          <input
            type="text"
            name="stateName"
            value={formData.stateName}
            onChange={handleChange}
          />
          {errors.stateName && <p style={{ color: "red" }}>{errors.stateName}</p>}
        </div>
        <button type="submit">Add State</button>
      </form>

      <h3>States</h3>
      <ul>
        {displayData.map((state) => (
          <li key={state.id}>
            {state.state_name} - {state.country_name}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default State;
