import { useCallback, useEffect, useState } from "react";
import { NavLink, useNavigate } from 'react-router-dom';

function Register() {
    const navigate = useNavigate();

    const [formData, setFormData] = useState({
        name: '', organization: '', email: '', password: '', numUsers: '',
        mobile: '', country: '', state: '', expireDays: '', photo: null, 
        address: '', jobTitle: '', company: '', zipCode: '', dateOfBirth: '' // New fields added
    });
    const [errors, setErrors] = useState({});
    const [displayList, setDisplayList] = useState([]);
    const [editId, setEditId] = useState(null);
    const [isEditing, setIsEditing] = useState(false);
    const [countries, setCountries] = useState([]);
    const [states, setStates] = useState([]);

    useEffect(() => {
        const fetchCountries = async () => {
            const response = await fetch('/api/countries');
            const data = await response.json();
            setCountries(data);
        };
        fetchCountries();
    }, []);

    const handleCountryChange = async (e) => {
        const selectedCountry = e.target.value;
        setFormData({ ...formData, country: selectedCountry });
        
        const response = await fetch(`/api/states/${selectedCountry}`);
        const data = await response.json();
        setStates(data);
    };

    const handleInputChange = ({ target: { id, value, files } }) => {
        if (id === 'fil' && files && files.length > 0) {
            setFormData({ ...formData, photo: files[0] });
        } else {
            setFormData({ ...formData, [id]: value });
        }
        setErrors({ ...errors, [id]: '' });
    };

    const validateForm = () => {
        const formErrors = {};
        if (!formData.name) formErrors.name = "Required";
        if (!formData.organization) formErrors.organization = "Required";
        if (!formData.email) formErrors.email = !/\S+@\S+\.\S+/.test(formData.email) ? "Invalid Email" : "Required";
        if (!formData.password || formData.password.length < 6) formErrors.password = "Min 6 chars";
        if (!formData.numUsers || isNaN(formData.numUsers) || formData.numUsers <= 0) formErrors.numUsers = "Invalid number";
        if (!formData.mobile || !/^\d{10}$/.test(formData.mobile)) formErrors.mobile = "Invalid mobile";
        if (!formData.country) formErrors.country = "Required";
        if (!formData.state) formErrors.state = "Required";
        if (!formData.expireDays || isNaN(formData.expireDays) || formData.expireDays <= 0) formErrors.expireDays = "Invalid days";
        if (!formData.address) formErrors.address = "Required"; // Validation for the new field
        if (!formData.jobTitle) formErrors.jobTitle = "Required"; // Validation for the new field
        if (!formData.company) formErrors.company = "Required"; // Validation for the new field
        if (!formData.zipCode || !/^\d{5}$/.test(formData.zipCode)) formErrors.zipCode = "Invalid zip code"; // Validation for new field
        if (!formData.dateOfBirth) formErrors.dateOfBirth = "Required"; // Validation for new field

        setErrors(formErrors);
        return !Object.keys(formErrors).length;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (validateForm()) {
            const formPayload = new FormData();
            Object.keys(formData).forEach(key => formPayload.append(key, formData[key]));

            const method = editId ? 'PUT' : 'POST';
            const url = editId ? `/api/users/${editId}` : '/api/users';

            try {
                const response = await fetch(url, {
                    method,
                    body: formPayload,
                });
                if (response.ok) {
                    alert(editId ? "Form updated successfully!" : "Form submitted successfully!");
                    setFormData({ name: '', organization: '', email: '', password: '', numUsers: '', mobile: '', country: '', state: '', expireDays: '', photo: null, address: '', jobTitle: '', company: '', zipCode: '', dateOfBirth: '' });
                    document.getElementById("fil").value = "";
                    setEditId(null);
                    fetchData();
                } else {
                    console.error("Failed to submit form data.");
                }
            } catch (error) {
                console.error("Error:", error);
            }
        }
    };

    const fetchData = useCallback(async () => {
        try {
            const response = await fetch('/api/users');
            if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
            const data = await response.json();
            setDisplayList(data);
        } catch (error) {
            console.error('Fetch error:', error);
        }
    }, []);
    
    useEffect(() => {
        fetchData();
    }, [fetchData]);

    const handleEdit = (id) => {
        const user = displayList.find(user => user.id === id);
        setFormData(user);
        setEditId(id);
        setIsEditing(true);
    };

    const handleDelete = async (id) => {
        if (window.confirm("Are you sure you want to delete this user?")) {
            try {
                const response = await fetch(`/api/users/${id}`, { method: 'DELETE' });
                if (response.ok) {
                    alert("User deleted successfully");
                    fetchData();
                } else {
                    console.error("Failed to delete user.");
                }
            } catch (error) {
                console.error("Error:", error);
            }
        }
    };

    return (
        <div className="bg-gray-100 flex flex-col items-center">
            <div className="text-center mb-5">
                <h1 className="text-3xl font-bold mb-5">Registered Users (Router)</h1>
                <div className="flex justify-center gap-4 flex-wrap">
                    <NavLink to="/country" className="bg-blue-500 text-white font-bold py-2 px-4 rounded hover:bg-blue-600 mb-4">
                        Add Country
                    </NavLink>
                    <button onClick={() => { navigate('/state') }} className="bg-green-500 text-white font-bold py-2 px-4 rounded hover:bg-green-600 mb-4">
                        Add State
                    </button>
                </div>
            </div>
    
            <div className="flex flex-col space-y-4 w-full max-w-4xl mx-auto p-4">
                <form onSubmit={handleSubmit} className="bg-white p-6 rounded-lg shadow-md">
                    <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                        {/* Name */}
                        <div className="flex flex-col">
                            <label htmlFor="name">Name</label>
                            <input
                                id="name"
                                type="text"
                                value={formData.name}
                                onChange={handleInputChange}
                                className="border p-2"
                            />
                            {errors.name && <span className="text-red-500 text-sm">{errors.name}</span>}
                        </div>
                        {/* Organization */}
                        <div className="flex flex-col">
                            <label htmlFor="organization">Organization</label>
                            <input
                                id="organization"
                                type="text"
                                value={formData.organization}
                                onChange={handleInputChange}
                                className="border p-2"
                            />
                            {errors.organization && <span className="text-red-500 text-sm">{errors.organization}</span>}
                        </div>
                        {/* Email */}
                        <div className="flex flex-col">
                            <label htmlFor="email">Email</label>
                            <input
                                id="email"
                                type="email"
                                value={formData.email}
                                onChange={handleInputChange}
                                className="border p-2"
                            />
                            {errors.email && <span className="text-red-500 text-sm">{errors.email}</span>}
                        </div>
                        {/* Password */}
                        <div className="flex flex-col">
                            <label htmlFor="password">Password</label>
                            <input
                                id="password"
                                type="password"
                                value={formData.password}
                                onChange={handleInputChange}
                                className="border p-2"
                            />
                            {errors.password && <span className="text-red-500 text-sm">{errors.password}</span>}
                        </div>
                        {/* Num Users */}
                        <div className="flex flex-col">
                            <label htmlFor="numUsers">Number of Users</label>
                            <input
                                id="numUsers"
                                type="number"
                                value={formData.numUsers}
                                onChange={handleInputChange}
                                className="border p-2"
                            />
                            {errors.numUsers && <span className="text-red-500 text-sm">{errors.numUsers}</span>}
                        </div>
                        {/* Mobile */}
                        <div className="flex flex-col">
                            <label htmlFor="mobile">Mobile</label>
                            <input
                                id="mobile"
                                type="text"
                                value={formData.mobile}
                                onChange={handleInputChange}
                                className="border p-2"
                            />
                            {errors.mobile && <span className="text-red-500 text-sm">{errors.mobile}</span>}
                        </div>
                        {/* Country */}
                        <div className="flex flex-col">
                            <label htmlFor="country">Country</label>
                            <select
                                id="country"
                                value={formData.country}
                                onChange={handleCountryChange}
                                className="border p-2"
                            >
                                <option value="">Select Country</option>
                                {countries.map(country => (
                                    <option key={country.code} value={country.code}>{country.name}</option>
                                ))}
                            </select>
                            {errors.country && <span className="text-red-500 text-sm">{errors.country}</span>}
                        </div>
                        {/* State */}
                        <div className="flex flex-col">
                            <label htmlFor="state">State</label>
                            <select
                                id="state"
                                value={formData.state}
                                onChange={handleInputChange}
                                className="border p-2"
                            >
                                <option value="">Select State</option>
                                {states.map(state => (
                                    <option key={state.code} value={state.code}>{state.name}</option>
                                ))}
                            </select>
                            {errors.state && <span className="text-red-500 text-sm">{errors.state}</span>}
                        </div>
                        {/* Expiry Days */}
                        <div className="flex flex-col">
                            <label htmlFor="expireDays">Expiry Days</label>
                            <input
                                id="expireDays"
                                type="number"
                                value={formData.expireDays}
                                onChange={handleInputChange}
                                className="border p-2"
                            />
                            {errors.expireDays && <span className="text-red-500 text-sm">{errors.expireDays}</span>}
                        </div>
                        {/* Address */}
                        <div className="flex flex-col">
                            <label htmlFor="address">Address</label>
                            <input
                                id="address"
                                type="text"
                                value={formData.address}
                                onChange={handleInputChange}
                                className="border p-2"
                            />
                            {errors.address && <span className="text-red-500 text-sm">{errors.address}</span>}
                        </div>
                        {/* Job Title */}
                        <div className="flex flex-col">
                            <label htmlFor="jobTitle">Job Title</label>
                            <input
                                id="jobTitle"
                                type="text"
                                value={formData.jobTitle}
                                onChange={handleInputChange}
                                className="border p-2"
                            />
                            {errors.jobTitle && <span className="text-red-500 text-sm">{errors.jobTitle}</span>}
                        </div>
                        {/* Company */}
                        <div className="flex flex-col">
                            <label htmlFor="company">Company</label>
                            <input
                                id="company"
                                type="text"
                                value={formData.company}
                                onChange={handleInputChange}
                                className="border p-2"
                            />
                            {errors.company && <span className="text-red-500 text-sm">{errors.company}</span>}
                        </div>
                        {/* Zip Code */}
                        <div className="flex flex-col">
                            <label htmlFor="zipCode">Zip Code</label>
                            <input
                                id="zipCode"
                                type="text"
                                value={formData.zipCode}
                                onChange={handleInputChange}
                                className="border p-2"
                            />
                            {errors.zipCode && <span className="text-red-500 text-sm">{errors.zipCode}</span>}
                        </div>
                        {/* Date of Birth */}
                        <div className="flex flex-col">
                            <label htmlFor="dateOfBirth">Date of Birth</label>
                            <input
                                id="dateOfBirth"
                                type="date"
                                value={formData.dateOfBirth}
                                onChange={handleInputChange}
                                className="border p-2"
                            />
                            {errors.dateOfBirth && <span className="text-red-500 text-sm">{errors.dateOfBirth}</span>}
                        </div>
                    </div>
                    <div className="flex justify-center gap-4 mt-4">
                        <button type="submit" className="bg-blue-500 text-white py-2 px-6 rounded hover:bg-blue-600">{isEditing ? 'Update' : 'Add'} User</button>
                    </div>
                </form>
    
                {/* Display User List */}
                <div className="overflow-x-auto mt-6">
                    <table className="min-w-full table-auto border-collapse">
                        <thead>
                            <tr className="bg-gray-200">
                                <th className="border-2 border-green-300 px-4 py-2">ID</th>
                                <th className="border-2 border-green-300 px-4 py-2">Name</th>
                                <th className="border-2 border-green-300 px-4 py-2">Organization</th>
                                <th className="border-2 border-green-300 px-4 py-2">Email</th>
                                <th className="border-2 border-green-300 px-4 py-2">Mobile</th>
                                <th className="border-2 border-green-300 px-4 py-2">Country</th>
                                <th className="border-2 border-green-300 px-4 py-2">State</th>
                                <th className="border-2 border-green-300 px-4 py-2">Address</th>
                                <th className="border-2 border-green-300 px-4 py-2">Job Title</th>
                                <th className="border-2 border-green-300 px-4 py-2">Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {displayList.map(user => (
                                <tr key={user.id}>
                                    <td className="border-2 px-4 py-2">{user.id}</td>
                                    <td className="border-2 px-4 py-2">{user.name}</td>
                                    <td className="border-2 px-4 py-2">{user.organization}</td>
                                    <td className="border-2 px-4 py-2">{user.email}</td>
                                    <td className="border-2 px-4 py-2">{user.mobile}</td>
                                    <td className="border-2 px-4 py-2">{user.country}</td>
                                    <td className="border-2 px-4 py-2">{user.state}</td>
                                    <td className="border-2 px-4 py-2">{user.address}</td>
                                    <td className="border-2 px-4 py-2">{user.jobTitle}</td>
                                    <td className="border-2 px-4 py-2">
                                        <button onClick={() => handleEdit(user.id)} className="bg-blue-500 text-white px-4 py-2 rounded-md mr-2">
                                            Edit
                                        </button>
                                        <button onClick={() => handleDelete(user.id)} className="bg-red-500 text-white px-4 py-2 rounded-md">
                                            Delete
                                        </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    );
}

export default Register;
