import React, { useState, useEffect } from "react";

function State() {
    const [formData, setFormData] = useState({ country: '', stateName: '' });
    const [errors, setErrors] = useState({});
    const [displayData, setDisplayData] = useState([]);
    const [countryList, setCountryList] = useState([]);
    const [editId, setEditId] = useState(null);

    useEffect(() => {
        fetchCountries();
        fetchStates();
    }, []);

    const fetchCountries = async () => {
        try {
            const response = await fetch("http://localhost:5000/countries");
            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }
            const data = await response.json();
            setCountryList(data);
        } catch (error) {
            console.error("Error fetching countries:", error);
        }
    };

    const fetchStates = async () => {
        try {
            const response = await fetch("http://localhost:5000/states");
            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }
            const data = await response.json();
            console.log("Fetched States:", data); // Log the fetched data

            setDisplayData(data);
        } catch (error) {
            console.error("Error fetching states ---:", error);
        }
    };

    const handleInputChange = (e) => {
        const { id, value } = e.target;
        setFormData({ ...formData, [id]: value });
        setErrors((prevErrors) => ({ ...prevErrors, [id]: '' }));
    };

    const validateForm = () => {
        const formErrors = {};
        if (!formData.country) {
            formErrors.country = "Please select a country.";
        }
        if (!formData.stateName) {
            formErrors.stateName = "State name is required.";
        } else if (formData.stateName.length < 3) {
            formErrors.stateName = "State name should be at least 3 characters.";
        }
        setErrors(formErrors);
        return Object.keys(formErrors).length === 0;
    };

    // const handleSubmit = async (e) => {
    //     e.preventDefault();
    //     if (validateForm()) {
    //         try {
    //             const method = editId ? 'PUT' : 'POST';
    //             const response = await fetch(`http://localhost:5000/states${editId ? `/${editId}` : ''}`, {
    //                 method,
    //                 headers: { 'Content-Type': 'application/json' },
    //                 body: JSON.stringify(formData)
    //             });

    //             if (response.ok) {
    //                 alert(editId ? "State updated successfully!" : "State added successfully!");
    //                 fetchStates();
    //                 setFormData({ country: '', stateName: '' });
    //                 setEditId(null);
    //             } else {
    //                 console.error("Error submitting state.");
    //             }
    //         } catch (error) {
    //             console.error("Error:", error);
    //         }
    //     }
    // };


    // const handleSubmit = async (e) => {
    //   e.preventDefault(); // Prevents the default form submission behavior
      
    //   if (validateForm()) { // Validate the form data before submitting
    //     try {
    //       console.log("Form data to be sent:", formData); // Log form data to verify

    //       if (!editId) {
    //         // Add new state entry if no editId exists (i.e., creating new state)
    //         const response = await fetch('http://localhost:5000/states', {
    //           method: 'POST',
    //           headers: { 'Content-Type': 'application/json' },
    //           body: JSON.stringify(formData), // Send the form data as JSON
    //         });
    
    //         if (response.ok) {
    //           alert("State added successfully!"); // Success message
    //           fetchStates(); // Fetch the updated list of states after adding the new one
    //           setFormData({ stateCode: '', stateName: '' }); // Reset the form data
    //         } else {
    //           const errorData = await response.json(); // Get the response body for more details
    //           console.error("Error response:", errorData); // Log the error response
    //           alert("Failed to add state. Please try again."); // Error message if add fails
    //         }
    //       } else {
    //         // Update an existing state if editId exists
    //         const response = await fetch(`http://localhost:5000/states/${editId}`, {
    //           method: 'PUT',
    //           headers: { 'Content-Type': 'application/json' },
    //           body: JSON.stringify(formData), // Send the updated form data as JSON
    //         });
    
    //         if (response.ok) {
    //           alert("State updated successfully!"); // Success message
    //           fetchStates(); // Fetch the updated list of states after updating the existing one
    //           setFormData({ stateCode: '', stateName: '' }); // Reset the form data
    //           setEditId(null); // Clear the editId so it can be used for future entries
    //         } else {
    //           alert("Failed to update state. Please try again."); // Error message if update fails
    //         }
    //       }
    //     } catch (error) {
    //       console.error("Error submitting form:", error); // Log any errors to the console
    //     }
    //   }
    // };

    const handleSubmit = async (e) => {
      e.preventDefault();
      if (validateForm()) {
        try {
          console.log("Form data to be sent:", formData);
          const response = await fetch('http://localhost:5000/states', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(formData),
          });
    
          if (response.ok) {
            alert("State added successfully!");
            fetchStates();
            setFormData({ stateCode: '', stateName: '' });
          } else {
            const errorData = await response.json();  // Get detailed error response from the server
            console.error("Error response:", errorData);  // Log error response to console
            alert(`Failed to add state: ${errorData.message || "Please try again."}`); // Display detailed error message if available
          }
        } catch (error) {
          console.error("Error submitting form:", error);
          alert("An unexpected error occurred. Please try again later.");
        }
      }
    };
    
    
    const handleDelete = async (id) => {
        try {
            const response = await fetch(`http://localhost:5000/states/${id}`, { method: 'DELETE' });
            if (response.ok) {
                fetchStates();
                alert("State deleted successfully");
            }
        } catch (error) {
            console.error("Error deleting state:", error);
        }
    };

    const handleEdit = (id) => {
        const stateToEdit = displayData.find((state) => state.id === id);
        if (stateToEdit) {
            setFormData({ country: stateToEdit.country_name, stateName: stateToEdit.state_name });
            setEditId(id);
        }
    };

    return (
        <div className="bg-gray-100 flex items-center justify-center h-screen">
            <div className="bg-white p-6 rounded shadow-md w-96">
                <h2 className="text-2xl font-bold mb-4">{editId ? "Update" : "Add"} State</h2>
                <form onSubmit={handleSubmit}>
                    <div className="mb-4">
                        <label htmlFor="country" className="block text-sm font-medium text-gray-700">Select Country</label>
                        <select
                            id="country"
                            value={formData.country}
                            onChange={handleInputChange}
                            className="mt-1 block w-full p-2 border border-gray-300 rounded"
                        >
                            <option value="">Choose a country</option>
                            {countryList.map((country) => (
                                <option key={country.country_code} value={country.country_code}>
                                    {country.country_name}
                                </option>
                            ))}
                        </select>
                        {errors.country && <p className="text-red-500 text-sm">{errors.country}</p>}
                    </div>
                    <div className="mb-4">
                        <label htmlFor="stateName" className="block text-sm font-medium text-gray-700">State Name</label>
                        <input
                            type="text"
                            id="stateName"
                            value={formData.stateName}
                            onChange={handleInputChange}
                            className="mt-1 block w-full p-2 border border-gray-300 rounded"
                        />
                        {errors.stateName && <p className="text-red-500 text-sm">{errors.stateName}</p>}
                    </div>
                    <button type="submit" className="bg-blue-500 text-white font-bold py-2 px-4 rounded hover:bg-blue-600">
                        {editId ? "Update State" : "Add State"}
                    </button>
                </form>

                <h2 className="text-xl font-bold mt-6 mb-4">States List</h2>
                <table className="min-w-full bg-white border border-gray-300">
                    <thead>
                        <tr>
                            <th className="py-2 px-2 border-b text-left">ID</th>
                            <th className="py-2 px-2 border-b text-left">Country</th>
                            <th className="py-2 px-2 border-b text-left">State Name</th>
                            <th className="py-2 px-2 border-b text-left">Edit</th>
                            <th className="py-2 px-2 border-b text-left">Delete</th>
                        </tr>
                    </thead>
                    <tbody>
                        {displayData.map((state) => (
                            <tr key={state.id}>
                                <td className="py-2 px-2 border-b">{state.id}</td>
                                <td className="py-2 px-2 border-b">{state.country_name}</td>
                                <td className="py-2 px-2 border-b">{state.state_name}</td>
                                <td className="py-2 px-2 border-b">
                                    <button
                                        onClick={() => handleEdit(state.id)}
                                        className="text-blue-500 hover:text-blue-700"
                                    >
                                        Edit
                                    </button>
                                </td>
                                <td className="py-2 px-2 border-b">
                                    <button
                                        onClick={() => handleDelete(state.id)}
                                        className="text-red-500 hover:text-red-700"
                                    >
                                        Delete
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default State;
