import { useCallback, useEffect, useState } from "react";
import { NavLink, useNavigate } from 'react-router-dom';

function Register() {
    const navigate = useNavigate();

    const [formData, setFormData] = useState({
        name: '', organization: '', email: '', password: '', numUsers: '',
        mobile: '', country: '', state: '', expireDays: ''
    });
    const [errors, setErrors] = useState({});
    const [displayList, setDisplayList] = useState([]);
    const [editId, setEditId] = useState(null);
    const [isEditing, setIsEditing] = useState(false);
    const [countries, setCountries] = useState([]);
    const [states, setStates] = useState([]);

    useEffect(() => {
        fetchCountries();
        fetchData();
    }, []);

    const fetchCountries = async () => {
        try {
            const response = await fetch('/api/countries');
            if (response.ok) {
                const data = await response.json();
                setCountries(data);
            } else {
                console.error("Failed to fetch countries");
            }
        } catch (error) {
            console.error("Error fetching countries:", error);
        }
    };

    const handleCountryChange = async (e) => {
        const selectedCountry = e.target.value;
        setFormData({ ...formData, country: selectedCountry, state: '' });
        
        try {
            const response = await fetch(`/api/states/${selectedCountry}`);
            if (response.ok) {
                const data = await response.json();
                setStates(data);
            } else {
                console.error("Failed to fetch states");
            }
        } catch (error) {
            console.error("Error fetching states:", error);
        }
    };

    const handleInputChange = ({ target: { id, value } }) => {
        setFormData({ ...formData, [id]: value });
        setErrors({ ...errors, [id]: '' });
    };

    const validateForm = () => {
        const formErrors = {};
        if (!formData.name) formErrors.name = "Name is required.";
        if (!formData.organization) formErrors.organization = "Organization is required.";
        if (!formData.email) {
            formErrors.email = "Email is required.";
        } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
            formErrors.email = "Invalid email format.";
        }
        if (!formData.password || formData.password.length < 6) {
            formErrors.password = "Password must be at least 6 characters.";
        }
        if (!formData.numUsers || isNaN(formData.numUsers) || formData.numUsers <= 0) {
            formErrors.numUsers = "Number of users must be a positive number.";
        }
        if (!formData.mobile || !/^\d{10}$/.test(formData.mobile)) {
            formErrors.mobile = "Mobile number must be 10 digits.";
        }
        if (!formData.country) formErrors.country = "Country is required.";
        if (!formData.state) formErrors.state = "State is required.";
        if (!formData.expireDays || isNaN(formData.expireDays) || formData.expireDays <= 0) {
            formErrors.expireDays = "Expiration days must be a positive number.";
        }

        setErrors(formErrors);
        return Object.keys(formErrors).length === 0;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (validateForm()) {
            const method = editId ? 'PUT' : 'POST';
            const url = editId ? `/api/users/${editId}` : '/api/users';

            try {
                const response = await fetch(url, {
                    method,
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify(formData),
                });
                if (response.ok) {
                    alert(editId ? "User updated successfully!" : "User registered successfully!");
                    setFormData({
                        name: '', organization: '', email: '', password: '', numUsers: '',
                        mobile: '', country: '', state: '', expireDays: ''
                    });
                    setEditId(null);
                    setIsEditing(false);
                    fetchData();
                } else {
                    console.error("Failed to submit form data.");
                    alert("Failed to submit form data. Please try again.");
                }
            } catch (error) {
                console.error("Error submitting form:", error);
            }
        }
    };

    const fetchData = useCallback(async () => {
        try {
            const response = await fetch('/api/users');
            if (response.ok) {
                const data = await response.json();
                setDisplayList(data);
            } else {
                console.error("Failed to fetch users");
            }
        } catch (error) {
            console.error("Error fetching users:", error);
        }
    }, []);

    useEffect(() => {
        fetchData();
    }, [fetchData]);

    const handleEdit = (id) => {
        const user = displayList.find(user => user.id === id);
        setFormData(user);
        setEditId(id);
        setIsEditing(true);
    };

    const handleDelete = async (id) => {
        if (window.confirm("Are you sure you want to delete this user?")) {
            try {
                const response = await fetch(`/api/users/${id}`, { method: 'DELETE' });
                if (response.ok) {
                    alert("User deleted successfully");
                    fetchData();
                } else {
                    console.error("Failed to delete user.");
                    alert("Failed to delete user. Please try again.");
                }
            } catch (error) {
                console.error("Error deleting user:", error);
            }
        }
    };

    return (
        <div className="bg-gray-100 flex flex-col items-center p-6">
            <h1 className="text-3xl font-bold mb-6">Register Users</h1>
            <form onSubmit={handleSubmit} className="w-full max-w-lg bg-white p-6 rounded shadow-md">
                <div className="mb-4">
                    <label htmlFor="name" className="block text-gray-700 font-bold mb-2">Name</label>
                    <input
                        type="text"
                        id="name"
                        value={formData.name}
                        onChange={handleInputChange}
                        className="w-full p-2 border border-gray-300 rounded"
                    />
                    {errors.name && <p className="text-red-500 text-sm">{errors.name}</p>}
                </div>
                <div className="mb-4">
                    <label htmlFor="organization" className="block text-gray-700 font-bold mb-2">Organization</label>
                    <input
                        type="text"
                        id="organization"
                        value={formData.organization}
                        onChange={handleInputChange}
                        className="w-full p-2 border border-gray-300 rounded"
                    />
                    {errors.organization && <p className="text-red-500 text-sm">{errors.organization}</p>}
                </div>
                <div className="mb-4">
                    <label htmlFor="email" className="block text-gray-700 font-bold mb-2">Email</label>
                    <input
                        type="email"
                        id="email"
                        value={formData.email}
                        onChange={handleInputChange}
                        className="w-full p-2 border border-gray-300 rounded"
                    />
                    {errors.email && <p className="text-red-500 text-sm">{errors.email}</p>}
                </div>
                <div className="mb-4">
                    <label htmlFor="password" className="block text-gray-700 font-bold mb-2">Password</label>
                    <input
                        type="password"
                        id="password"
                        value={formData.password}
                        onChange={handleInputChange}
                        className="w-full p-2 border border-gray-300 rounded"
                    />
                    {errors.password && <p className="text-red-500 text-sm">{errors.password}</p>}
                </div>
                <div className="mb-4">
                    <label htmlFor="numUsers" className="block text-gray-700 font-bold mb-2">Number of Users</label>
                    <input
                        type="number"
                        id="numUsers"
                        value={formData.numUsers}
                        onChange={handleInputChange}
                        className="w-full p-2 border border-gray-300 rounded"
                    />
                    {errors.numUsers && <p className="text-red-500 text-sm">{errors.numUsers}</p>}
                </div>
                <div className="mb-4">
                    <label htmlFor="mobile" className="block text-gray-700 font-bold mb-2">Mobile</label>
                    <input
                        type="text"
                        id="mobile"
                        value={formData.mobile}
                        onChange={handleInputChange}
                        className="w-full p-2 border border-gray-300 rounded"
                    />
                    {errors.mobile && <p className="text-red-500 text-sm">{errors.mobile}</p>}
                </div>
                <div className="mb-4">
                    <label htmlFor="address" className="block text-gray-700 font-bold mb-2">Address</label>
                    <textarea
                        id="address"
                        value={formData.address}
                        onChange={handleInputChange}
                        className="w-full p-2 border border-gray-300 rounded"
                    />
                    {errors.address && <p className="text-red-500 text-sm">{errors.address}</p>}
                </div>
                <div className="mb-4">
                    <label htmlFor="country" className="block text-gray-700 font-bold mb-2">Country</label>
                    <select
                        id="country"
                        value={formData.country}
                        onChange={handleInputChange}
                        className="w-full p-2 border border-gray-300 rounded"
                    >
                        <option value="">Select a country</option>
                        {countries.map(country => (
                            <option key={country.code} value={country.code}>
                                {country.name}
                            </option>
                        ))}
                    </select>
                    {errors.country && <p className="text-red-500 text-sm">{errors.country}</p>}
                </div>
                <div className="mb-4">
                    <label htmlFor="state" className="block text-gray-700 font-bold mb-2">State</label>
                    <select
                        id="state"
                        value={formData.state}
                        onChange={handleInputChange}
                        className="w-full p-2 border border-gray-300 rounded"
                    >
                        <option value="">Select a state</option>
                        {states.map(state => (
                            <option key={state.code} value={state.code}>
                                {state.name}
                            </option>
                        ))}
                    </select>
                    {errors.state && <p className="text-red-500 text-sm">{errors.state}</p>}
                </div>
                <div className="mb-4">
                    <label htmlFor="expireDays" className="block text-gray-700 font-bold mb-2">Expire Days</label>
                    <input
                        type="number"
                        id="expireDays"
                        value={formData.expireDays}
                        onChange={handleInputChange}
                        className="w-full p-2 border border-gray-300 rounded"
                    />
                    {errors.expireDays && <p className="text-red-500 text-sm">{errors.expireDays}</p>}
                </div>
                <div className="flex justify-between mb-4">
                    <button
                        type="submit"
                        className="bg-blue-500 text-white p-2 rounded"
                    >
                        {isEditing ? 'Update User' : 'Register User'}
                    </button>
                    <NavLink to="/users" className="bg-gray-500 text-white p-2 rounded">Back</NavLink>
                </div>
            </form>
            <div className="mt-6 w-full max-w-lg">
                <h2 className="text-2xl font-bold mb-4">Registered Users</h2>
                <table className="w-full table-auto border-collapse">
                    <thead>
                        <tr>
                            <th className="px-4 py-2 border">Name</th>
                            <th className="px-4 py-2 border">Organization</th>
                            <th className="px-4 py-2 border">Email</th>
                            <th className="px-4 py-2 border">Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {displayList.map(user => (
                            <tr key={user.id}>
                                <td className="px-4 py-2 border">{user.name}</td>
                                <td className="px-4 py-2 border">{user.organization}</td>
                                <td className="px-4 py-2 border">{user.email}</td>
                                <td className="px-4 py-2 border">
                                    <button
                                        onClick={() => handleEdit(user.id)}
                                        className="bg-yellow-500 text-white p-2 rounded mr-2"
                                    >
                                        Edit
                                    </button>
                                    <button
                                        onClick={() => handleDelete(user.id)}
                                        className="bg-red-500 text-white p-2 rounded"
                                    >
                                        Delete
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default Register;
