import './App.css';
import { useState, useEffect } from 'react';

function App() {
  const [formdata, setFormdata] = useState({ name: '', file: null });
  const [records, setRecords] = useState([]);

  // Fetch existing records from the backend
  const fetchRecords = async () => {
    try {
      const response = await fetch('http://localhost:5555/records');
      if (response.ok) {
        setRecords(await response.json());
      } else {
        console.error('Failed to fetch records:', response.statusText);
      }
    } catch (error) {
      console.error('Fetch error:', error);
    }
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    const payload = {
      name: formdata.name,
      file: formdata.file, // Send the file as part of the JSON object
    };

    try {
      const response = await fetch('http://localhost:5555/sample', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(payload),
      });
      if (response.ok) {
        fetchRecords(); // Refresh records after successful upload
      } else {
        console.error('Upload failed:', response.statusText);
      }
    } catch (error) {
      console.error('Upload error:', error);
    }
  };

  // Dynamic input change handler
  const handleInputChange = ({ target: { id, value, files } }) => {
    if (files && files.length > 0) {
      setFormdata({ ...formdata, [id]: files[0] });
    } else {
      setFormdata({ ...formdata, [id]: value });
    }
  };

  // Initial fetch of records when the component mounts
  useEffect(() => {
    fetchRecords();
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <form onSubmit={handleSubmit}>
          <input
            id="name"
            type="text"
            placeholder="Name"
            value={formdata.name}
            required
            onChange={handleInputChange}
          />
          <input
            id="file"
            type="file"
            required
            onChange={handleInputChange}
          />
          <button type="submit">Submit</button>
        </form>

        <h2>Uploaded Records</h2>
        <table border="1">
          <thead>
            <tr>
              <th>Name</th>
              <th>File</th>
            </tr>
          </thead>
          <tbody>
            {records.map((record) => (
              <tr key={record.id}>
                <td>{record.name}</td>
                <td>
                  <img
                    src={`http://localhost:5555/uploads/${record.file_path}`}
                    alt={record.name}
                    style={{ maxWidth: '100px' }}
                  />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </header>
    </div>
  );
}

export default App;
