import logo from './logo.svg';
import './App.css';
import { useState, useEffect } from 'react';

function App() {
  const [formdata, setformdata] = useState({ name: '' });
  const [file, setFile] = useState(null);
  const [records, setRecords] = useState([]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();
    data.append('name', formdata.name);
    data.append('file', file);

    try {
      const response = await fetch('http://localhost:5555/sample', {
        method: 'POST',
        body: data,
      });

      if (response.ok) {
        console.log('File uploaded successfully');
        fetchRecords(); // Refresh records after submission
      } else {
        console.log('File upload failed');
      }
    } catch (error) {
      console.error('Error uploading file:', error);
    }
  };

  const fetchRecords = async () => {
    try {
      const response = await fetch('http://localhost:5555/records');
      const data = await response.json();
      setRecords(data);
    } catch (error) {
      console.error('Error fetching records:', error);
    }
  };

  useEffect(() => {
    fetchRecords(); // Fetch records on component mount
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <form onSubmit={handleSubmit}>
          <label htmlFor="name">Enter Name</label>
          <input
            type="text"
            id="name"
            name="name"
            value={formdata.name}
            required
            onChange={(e) => {
              setformdata({ ...formdata, [e.target.name]: e.target.value });
            }}
          />
          <br />
          <label htmlFor="file">Upload File</label>
          <input
            type="file"
            id="file"
            name="file"
            required
            onChange={(e) => setFile(e.target.files[0])}
          />
          <br />
          <button type="submit">Submit</button>
        </form>

        <h2>Uploaded Records</h2>
        {/* <table border="1">
          <thead>
            <tr>
              <th>Name</th>
              <th>File</th>
            </tr>
          </thead>
          <tbody>
            {records.map((record) => (
              <tr key={record.id}>
                <td>{record.name}</td>
                <td>
                  <a
                    href={`http://localhost:5555/uploads/${record.file_path}`}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    {record.file_path}
                  </a>
                </td>
              </tr>
            ))}
          </tbody>
        </table> */}

<table border="1">
  <thead>
    <tr>
      <th>Name</th>
      <th>File</th>
    </tr>
  </thead>
  <tbody>
    {records.map((record) => (
      <tr key={record.id}>
        <td>{record.name}</td>
        <td>
          <img
            src={`http://localhost:5555/uploads/${record.file_path}`}
            alt={record.name}
            style={{ maxWidth: '100px', maxHeight: '100px' }}
          />
        </td>
      </tr>
    ))}
  </tbody>
</table>

        <img src={logo} className="App-logo" alt="logo" />
      </header>
    </div>
  );
}

export default App;
