import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

function App() {
  const [formdata, setformdata] = useState({ name: '' });
  const [file, setFile] = useState(null);
  const [uploadedFile, setUploadedFile] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();
    data.append('name', formdata.name);
    data.append('file', file);

    try {
      const response = await fetch('http://localhost:5555/sample', {
        method: 'POST',
        body: data,
      });

      if (response.ok) {
        console.log('File uploaded successfully');
        const responseData = await response.json();
        setUploadedFile(responseData.fileUrl); // Update with backend response
      } else {
        console.log('File upload failed');
      }
    } catch (error) {
      console.error('Error uploading file:', error);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <form onSubmit={handleSubmit}>
          <label htmlFor="name">Enter Name</label>
          <input
            type="text"
            id="name"
            name="name"
            value={formdata.name}
            required
            onChange={(e) => {
              setformdata({ ...formdata, [e.target.name]: e.target.value });
            }}
          />
          <br />
          <label htmlFor="file">Upload File</label>
          <input
            type="file"
            id="file"
            name="file"
            required
            onChange={(e) => setFile(e.target.files[0])}
          />
          <br />
          <button type="submit">Submit</button>
        </form>
        {uploadedFile && (
          <div>
            <p>Uploaded File:</p>
            <img src={`http://localhost:5555/uploads/${uploadedFile}`} alt="Uploaded" style={{ maxWidth: '200px' }} />
          </div>
        )}
        <img src={logo} className="App-logo" alt="logo" />
      </header>
    </div>
  );
}

export default App;
